import React, { Component } from "react";
import { View, ScrollView, Text, StyleSheet } from "react-native";
import { ListItem, Icon } from "react-native-elements";

export default class List extends Component {
  render() {
    let list = this.props.source;
    return (
      <View>
        {list.map((item, i) => (
          <ListItem 
            bottomDivider 
            key={i}
            onPress={()=>{
                if(this.props.section > 0){
                    //no more lists, go to detail page
                    return(
                        <View>
                            <ScrollView contentContainerStyle={styles.contentContainer}>
                                <Detail source={this.page} image="" isArray={true} />
                            </ScrollView>
                        </View>
                    )
                } else {
                    this.props.navigation.push("Page", {
                        menu: this.props.menu, //no more menu from any list click
                        book: this.props.book == 0 ? item.id : this.props.bookID, //if is not a menu and no book id, set book id
                        section: this.props.book > 0 ? item.id : 0 //if is not a menu and has book ID, set section id
                    });
                }              
            }}
          >
            <Icon name='library-books' color="#bbbbbb" style={{padding: 5, paddingRight: 20}} />
            <ListItem.Content>
                <ListItem.Title>{item.title}</ListItem.Title>
                {item.brief ? <ListItem.Subtitle>{item.brief}</ListItem.Subtitle> : null}
            </ListItem.Content>
            <ListItem.Chevron />
          </ListItem>
        ))}
      </View>
    );
  }
}
