import React, { Component } from "react";
import { View, TouchableOpacity, Text, StyleSheet } from "react-native";
import Animated from "react-native-reanimated";
import { createMaterialTopTabNavigator } from "@react-navigation/material-top-tabs";
import foData from "../content/data.json";
import foMenu from "../content/menu.json";

import ErrorScreen from "../error/error";
import ContentScreen from "./content";

const Tab = createMaterialTopTabNavigator();
export default class TabBar extends Component {
  render() {
    this.list = this.props.menu == true ? foMenu : foData;
    return (
      <Tab.Navigator
        tabBarOptions={{
          tabStyle: { width: 'auto', color: 'blue' }
        }}
        tabBar={(props) => <BookSection {...props} source={foMenu} />}
      >
        {
          this.list.map((item, i) => (
            <Tab.Screen name={"menu" + item.id} options={{ title: item.title }}>
              {props => <ContentScreen {...props} source={item.list} />}
            </Tab.Screen>
          ))
        }
      </Tab.Navigator>
    );
  }
}

function BookSection({ state, descriptors, navigation, position }) {
  return (
    <View style={{ flexDirection: "row", backgroundColor: "#b32400", padding: 20 }}>
      {state.routes.map((route, index) => {
        const { options } = descriptors[route.key];
        const label =
          options.tabBarLabel !== undefined
            ? options.tabBarLabel
            : options.title !== undefined
            ? options.title
            : route.name;

        const isFocused = state.index === index;

        const onPress = () => {
          const event = navigation.emit({
            type: "tabPress",
            target: route.key,
          });

          if (!isFocused && !event.defaultPrevented) {
            console.log();
            navigation.navigate(route.name);
          }
        };

        const onLongPress = () => {
          navigation.emit({
            type: "tabLongPress",
            target: route.key,
          });
        };
        // modify inputRange for custom behavior
        const inputRange = state.routes.map((_, i) => i);
        const opacity = Animated.interpolate(position, {
          inputRange,
          outputRange: inputRange.map((i) => (i === index ? 1 : 0.5)),
        });

        return (
          <TouchableOpacity
            accessibilityRole="button"
            accessibilityStates={isFocused ? ["selected"] : []}
            accessibilityLabel={options.tabBarAccessibilityLabel}
            onPress={onPress}
            onLongPress={onLongPress}
            style={{ flex: 1 }}            
          >
            <Animated.Text numberOfLines = {1} style={{ opacity, color: "#ffffff", textAlign: "center" }}>{label}</Animated.Text>
          </TouchableOpacity>
        );
      })}
    </View>
  );
}
