import React, { Component, useState } from 'react';
import { View, ScrollView, Text, StyleSheet } from 'react-native';
import Listitem from "./list";
import Detail from "./detail";
import foData from "../content/data.json";
import foMenu from "../content/menu.json";

export default class Content extends Component {    
  render(){    
    //set default content
    let content = foMenu.find(item => item.id == this.props.menu);
    //set book content by section id
    if (this.props.menu > 0 && this.props.book > 0) {
      let book = foData.find(item => item.id == this.props.book);
      if(book){
        content = book.sections.find(s => s.id == this.props.section);
      }
    } 
    this.list = typeof content.list === "undefined" ? [] : content.list;
    this.page = typeof content.page === "undefined" ? [] : content.page;
    this.image = typeof content.image === "undefined" ? "" : content.image;
    this.media = typeof content.media === "undefined" ? "" : content.media;

    //console.log("content: " + this.list.length + ", " + typeof this.page)

    if (this.list.length == 0) {
      //return page content
      return(
        <View>
          <ScrollView contentContainerStyle={styles.contentContainer}>
            <Detail source={this.page} image={this.image} />
          </ScrollView>
        </View>
      )
    } else {
      //return list
      // console.log("content: " + this.list.length + ", " + this.page.length + ", " + this.image);
      // console.log("debug: " + this.props.menu + ", " + this.props.book + ", " + this.props.section + ", " + this.props.navigation);
      return (      
        <View>
          <ScrollView contentContainerStyle={styles.contentContainer}>
            <Listitem 
              source={this.list}
              menu={this.props.menu}
              book={this.props.book}
              section={this.props.section}
              navigation={this.props.navigation} 
            />
          </ScrollView>
        </View>
      );
    }
  }
}
const styles = StyleSheet.create({
  contentContainer: {
    marginBottom: 50
  }
});
